pragma solidity 0.5.11;

/**
 * @title A basic contract
 * @notice A notice for the basic contract
 * @dev Some dev stuff for the basic contract
 * @author Clemlak
 */
contract BasicExample {
    uint256 private number = 2;
    bool public isContractReady = false;

    /**
     * @notice Sets a number
     * @dev This is an external function
     * @param newNumber The new value of the number
     */
    function setNumber(uint256 newNumber) external {
        number = newNumber;
    }

    /**
     * @notice Gets the number
     * @dev This is an external view function
     * @return The value of the number
     */
    function getNumber() external view returns (uint256) {
        return number;
    }

    /**
     * @notice Gets two numbers
     * @dev This function looks complex
     * @return A cool number
     * @return Another cool number
     */
    function getTwoNumbers() external pure returns (
        uint256,
        uint256
    ) {
        return (
            42,
            15
        );
    }

    /**
     * @notice Add two numbers
     * @dev This function looks complex
     * @param a A number
     * @param b Another number
     * @return The sum of a + b
     */
    function add(
        uint256 a,
        uint256 b
    ) external pure returns (
        uint256
    ) {
        return a + b;
    }

    /**
     * @notice A private function
     * @dev This function is private, shush
     * @param a A number
     * @return The number multiplied by 2
     */
    function somePrivateFunction(
        uint256 a
    ) private pure returns (
        uint256
    ) {
        return a * 2;
    }

    modifier onlyIfContractIsReady() {
        require(isContractReady, "Contract not ready");
        _;
    }
}

